import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'Webflix';
}
<div class="site">
    <header>
        <app-navigation/>
    </header>
    <div class="main">
        <router-outlet/>
    </div>
    <footer>
        <app-footer/>
    </footer>
</div>
